function App() {

  const inTheaters: movieDTO[] = [{
    id: 1,
    title: 'Spider-Man: Far From Home',
    poster: 'https://upload.wikimedia.org/wikipedia/en/b/bd/Spider-Man_Far_From_Home_poster.jpg'
