import React, { useEffect, useState } from "react";
import "./App.css";

function App() {
  const [myDate, myDateUpdate] = useState(new Date());

  useEffect(() => {
    const intervalId = setInterval(() => {
      myDateUpdate(new Date());
    });
  });

  return (
    <div>
      <h3>Example React</h3>
      <input />
      <div>{myDate.toString()}</div>
    </div>
  );
}

export default App;
