export default function MapExample() {
  // const arr = [1, 2, 3, 4, 5];
  const arr = Array(100).fill(0); // [0, 0, 0, 0 ..., 0]

  return (
    <>
      <h1>Iteration example</h1>

      <select
        onChange={(e) => {
          console.log(e.currentTarget.value);
        }}
      >
        {arr.map((number, index) => (
          <option key={index + 1} value={index + 1}>
            {index + 1}
          </option>
        ))}
      </select>
      <br />
      <br />
    </>
  );
}
